$(function() {
	var bookId = $('body').attr("bookId");
	var chapterId = $('body').attr("chapterId");
	var lastChapterId = $('body').attr("lastChapterId");
	var nextChapterId = $('body').attr("nextChapterId");
	$(document)
			.keyup(
					function(event) {
						if (event.keyCode == $.ui.keyCode.LEFT) {
							if (lastChapterId == chapterId) {
								if (confirm("是否返回书页")) {
									document.location = "http://book.zongheng.com/book/"
											+ bookId + ".html";
								}
							} else {
								document.location = "http://book.zongheng.com/chapter/"
										+ bookId
										+ "/"
										+ lastChapterId
										+ ".html";
							}
						}
						if (event.keyCode == $.ui.keyCode.RIGHT) {
							if (nextChapterId == chapterId) {
								document.location = "http://book.zongheng.com/readmore/"
										+ bookId + ".html";
							} else {
								document.location = "http://book.zongheng.com/chapter/"
										+ bookId
										+ "/"
										+ nextChapterId
										+ ".html";
							}
						}
					});
});